package day8;

public class factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i,num=1;  
		  int number=2;
		  //It is the number to calculate factorial
		  
		  for(i=1; i<=number; i++){  
			  
		      num = num*i;    
		  }    
		  System.out.println("Factorial of "+number+" is: "+num);

	}

}
